let popupBg = document.querySelector(".popup__bg"),
    popup = document.querySelector(".popup"),
    openPopupButton = document.querySelectorAll(".open-popup"),
    closePopupButton = document.querySelector(".close-popup");
openPopupButton.forEach(e => { e.addEventListener("click", e => { e.preventDefault(), popupBg.classList.add("active"), popup.classList.add("active") }) }), closePopupButton.addEventListener("click", () => { popupBg.classList.remove("active"), popup.classList.remove("active") }), document.addEventListener("click", e => { e.target === popupBg && (popupBg.classList.remove("active"), popup.classList.remove("active")) });
